export interface ILoan {
    id?: string;
    customer: {
        id: string;
        identityDocument?: {
            type: string;
            number: string;
        };
    };
    type: string;
    amount: number;
    amountFinanced: number;
    currency: string;
    term: number;
    interestRate: number;
    annualNominalRate: string;
    lifeInsurance: {
        amount: number;
        startDate?: number;
        endDate?: number;
    };
    status: string;
    paymentDay: number;
    lead?: {
        id: string;
    };
    installment?: {
        id: string;
    };
    prepaymentType?: string;
    prepaymentMethod?: string;
    value_x_api_key?: string;
    transaction?: {
        id: string;
    };
    creationDate: number;
    lastUpdateDate: number;
    activationDate: number;
    lastMambuUpdateDate: number;
    metadata?: Partial<{
        "external.id": string;
        "external.cashier": string;
        "external.seller": string;
        "external.branch": string;
        [key: string]: any;
    }>;
}
export interface IInstallment {
    id: string;
    loan: {
        id: string;
        sequential?: string;
    };
    customer: {
        id: string;
    };
    dueDate: number;
    principalDue: number;
    interestDue: number;
    feeDue: number;
    penaltyDue: number;
    totalDue: number;
    status: string;
    creationDate: number;
    lastUpdateDate: number;
    number: string;
    pendingFee?: number;
}
export interface ITransaction {
    id: string;
    loan: {
        id: string;
    };
    customer: {
        id: string;
    };
    payment?: {
        id: string;
    };
    installments?: {
        id: string;
    }[];
    prepaymentType: string;
    amount: number;
    currency: string;
    prepaymentMethod: string;
    status: string;
    opCode: number;
    type: string;
    creationDate: number;
    lastUpdateDate: number;
}
export interface ISimulation {
    id: string;
    disbursementDate: number;
    customer: {
        identityDocument: {
            type: string;
            number: string;
        };
    };
    loan: Partial<ILoan>;
    checkForPromotions: boolean;
    lead: {
        id: string;
    };
    term: number;
    order: {
        items: {
            sku: string;
            quantity: number;
            amount?: number;
            interestRate?: number;
            annualNominalRate?: string;
        }[];
    };
    metadata: Partial<{
        "external.id": string;
        "external.seller": string;
        "external.branch": string;
    }>;
    creationDate: number;
    lastUpdateDate: number;
}
